package P0392_IsSubsequence;

import org.junit.Assert;
import org.junit.Test;

/**
 * Title: Solution1
 * Description:
 * 判断s是否为t的子序列，且s的子序列按照t的顺序有序排列。
 * 使用双指针Two Pointers处理
 *
 * @author Lin Hui
 * Created on 2019-12-28 22:11
 */
public class Solution1 {
    @Test
    public void testCase() {
        Assert.assertTrue(isSubsequence("abc", "ahbgdc"));
        Assert.assertFalse(isSubsequence("aec", "abcde"));
        Assert.assertTrue(isSubsequence("", "abcdefg"));
        Assert.assertTrue(isSubsequence("aa", "fragvra"));
        Assert.assertTrue(isSubsequence("leeeeetcode", ""));
    }

    public boolean isSubsequence(String s, String t) {
        int i = 0;
        int j = 0;
        char[] ss = s.toCharArray();
        char[] tt = t.toCharArray();
        while (i < ss.length && j < tt.length) {
            if (ss[i] == tt[j]) {
                i++;
            }
            j++;
        }
        return i == ss.length;
    }
}
